"use strict";

const build = require('../utils/build');

exports.command = 'build';
exports.describe = 'Builds your project for deployment on the Moovweb XDN.';
exports.builder = {
  skipFramework: {
    type: 'boolean',
    describe: 'Skips running the framework build (Next.js, Vue, Angular, etc...) and uses the existing build instead.'
  },
  angularProject: {
    type: 'string',
    describe: 'The Angular project to target for the server build. Defaults to the project specified as `defaultProject` in angular.json.'
  }
};

exports.handler = async yargs => {
  await build(yargs);
};