"use strict";

const detectFramework = require('../frameworks/detectFramework');

const {
  installDependencies
} = require('./packageManager');

const addIgnore = require('./addIgnore');

const get = require('lodash/get');

const resolveInPackage = require('./resolveInPackage');

const logo = require('./logo');

module.exports = async function init({
  context
}) {
  const {
    logger
  } = context;
  const framework = await detectFramework(); // add @xdn/* as built time dependencies

  const devDependencies = {
    '@xdn/core': 'latest',
    '@xdn/cli': 'latest',
    '@xdn/prefetch': 'latest'
  };

  if (framework) {
    devDependencies[framework.builder] = 'latest';
  }

  await installDependencies(devDependencies, {
    dev: true
  }); // add extra dependencies as runtime dependencies

  if (framework && framework.addDependencies) {
    const dependencies = {};
    framework.addDependencies.forEach(lib => {
      dependencies[lib] = 'latest';
    });
    await installDependencies(dependencies);
  }

  addIgnore();
  const initScript = resolveInPackage(get(framework, 'builder', '@xdn/core'), `bin/init`);
  await require(initScript)();
  logger.title(`\nðŸŽ‰ Your app is now ready to deploy on the ${logo}!\n`);
};